import React, { useState } from 'react';
import './Style.css';
import { NavLink as Link } from 'react-router-dom';
import Dropdown from './Dropdown/Dropdown';

function Sections({ click, closeMobileMenu }) {
	return (
		<ul className={click ? 'nav-menu active' : 'nav-menu'}>
			<li className='nav-item'>
				<Link className='nav-links' to='/InternshipReport/Remerciements' onClick={closeMobileMenu}>
					Remerciements
				</Link>
			</li>
			<li className='nav-item'>
				<Link className='nav-links' to='/InternshipReport/Plan' onClick={closeMobileMenu}>
					Plan
				</Link>
			</li>
			<li className='nav-item'>
				<Link className='nav-links' to='/InternshipReport/Introduction' onClick={closeMobileMenu}>
					Introduction
				</Link>
			</li>
			<li className='nav-item'>
				<Link className='nav-links' to='/InternshipReport/Développement' onClick={closeMobileMenu}>
					Développement
				</Link>
			</li>
			<li	className='nav-item' onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
				<Link to='/services' className='nav-links' onClick={closeMobileMenu}>
					Teams <i className='fas fa-caret-down' />
				</Link>
				{dropdown && <Dropdown />}
			</li>
			<li className='nav-item'>
				<Link className='nav-links' to='/InternshipReport/Conclusion' onClick={closeMobileMenu}>
					Conclusion
				</Link>
			</li>
		</ul>
	);
}

function Navbar({ handleClick, closeMobileMenu, click }) {
	const [dropdown, setDropdown] = useState(false);
	const onMouseEnter = () => {
		if (window.innerWidth < 960) {
		  setDropdown(false);
		} else {
		  setDropdown(true);
		}
	  };
	
	  const onMouseLeave = () => {
		if (window.innerWidth < 960) {
		  setDropdown(false);
		} else {
		  setDropdown(false);
		}
	  };
	return (
		<main>
			<nav className='navbar'>
				<Link to='/InternshipReport' className='link' onClick={closeMobileMenu}>
					InternshipReport
					<i className='fab fa-firstdraft' />
				</Link>
				<div className='menu-icon' onClick={handleClick}>
					<i className={click ? 'fas fa-times' : 'fas fa-bars'} />
				</div>
				<Sections click={click} closeMobileMenu={closeMobileMenu}/>
			</nav>
		</main>
	);
}

export default Navbar;
