import React, { useState } from 'react';
import { NavLink as Link } from 'react-router-dom';
import './Style.css';
import Dropdown from './Dropdown/Dropdown';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
// import 'bootstrap/dist/css/bootstrap.css'

function NavBar2({ handleClick, closeMobileMenu, click }) {
	const [dropdown, setDropdown] = useState(false);
	const onMouseEnter = () => {
		window.innerWidth < 960 ? setDropdown(false) : setDropdown(true);
	};
	const onMouseLeave = () => {
		window.innerWidth < 960 ? setDropdown(false) : setDropdown(false);
	};
	return (
		<main>
		<nav className='navbar'>
			<Link to='/InternshipReport' className='link' onClick={closeMobileMenu}>
				InternshipReport
				<i className='fab fa-firstdraft' />
			</Link>
			<div className='menu-icon' onClick={handleClick}>
				<i className={click ? 'fas fa-times' : 'fas fa-bars'} />
			</div>
			<ul className={click ? 'nav-menu active' : 'nav-menu'}>
				<li className='nav-item'>
					<Link to='/InternshipReport/Remerciements' className='nav-links' onClick={closeMobileMenu}>
						Remerciements
					</Link>
				</li>
				<li className='nav-item'>
					<Link to='/InternshipReport/Plan' className='nav-links' onClick={closeMobileMenu}>
						Plan<NavDropdown title="Dropdown" id="basic-nav-dropdown"></NavDropdown>
					</Link>
				</li>
				<li className='nav-item'>
					<Link to='/InternshipReport/Développements' className='nav-links' onClick={closeMobileMenu}>
						Développements
					</Link>
				</li>
				<li className='nav-item' onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
					<Link to='/InternshipReport/Teams' className='nav-links' onClick={closeMobileMenu}>
						Teams <i className='fas fa-caret-down' />
					</Link>
					{dropdown && <Dropdown />}
				</li>
				<li className='nav-item'>
					<Link to='/InternshipReport/Conclusion' className='nav-links' onClick={closeMobileMenu}>
						Conclusion
					</Link>
				</li>
			</ul>
		</nav>
		</main>
	);
}

